window.onload=function(){document.querySelectorAll(".privacy__block-link").forEach((t=>{t.addEventListener("click",(()=>{let e=t.previousElementSibling;e.classList.contains("show-text")?(e.classList.remove("show-text"),t.textContent="читать далее"):(e.classList.add("show-text"),t.textContent="свернуть")}))}))};